mapper.select:

SELECT cno, eno
    FROM couponmember
    WHERE mno = #{mno}


    SELECT cno, eno, cdate, cstate 
    	FROM eventcoupon
	    WHERE eno = #{eno}
	    and cno = #{cno}

mapper.update:
UPDATE
event
SET ecouponremain = #{remainCoupons}
