mapper.select:

SELECT rnum, mno, ono, ostate, oamount, pcode, pcolor, psize
	FROM(
		SELECT ROWNUM as rnum, mno, ono, ostate, oamount, pcode, pcolor, psize
			FROM (
				SELECT p.mno as mno, p.ono as ono, p.ostate as ostate, i.oamount as oamount,
			        SUBSTR(scode, 1, INSTR(scode,'_')-1) as pcode,
			        SUBSTR(scode,INSTR(scode,'_')+1, 2) as pcolor,
			        SUBSTR(scode, INSTR(scode,'_', -1)+1) as psize
				FROM productorder p, productorderitem i
				WHERE p.ono = i.ono
				AND p.mno = #{mno}
				ORDER BY p.ono DESC
				)
			WHERE rownum <= #{pager.endRowNo}
		)
	WHERE rnum >= #{pager.startRowNo}

SELECT COUNT(ono)
	FROM productorder
	WHERE mno = #{mno}
