mapper.select:
SELECT rnum, mno, ono, ostate, oamount, pcode, pcolor, psize, pname FROM ( SELECT ROWNUM as rnum, A.mno, A.ono, A.ostate, A.oamount, A.pcode, A.pcolor, A.psize, B.pname FROM product B, ( SELECT p.mno as mno, p.ono as ono, p.ostate as ostate, i.oamount as oamount, SUBSTR(scode, 1, INSTR(scode,'_')-1) as pcode, SUBSTR(scode,INSTR(scode,'_')+1, 2) as pcolor, SUBSTR(scode, INSTR(scode,'_', -1)+1) as psize FROM productorder p, productorderitem i WHERE p.ono = i.ono AND p.mno = #{mno} ORDER BY p.ono DESC) A WHERE A.pcode = B.pcode) WHERE rownum <= #{pager.endRowNo}
AND pcode LIKE '%'||#{keyword}||'%'
AND pname LIKE '%'||#{keyword}||'%'
AND rnum >= #{pager.startRowNo}

SELECT COUNT(ono)
	FROM productorder
	WHERE mno = #{mno}
