mapper.select:
SELECT p.pcode, p.pname, p.pprice, p.pdetail,
	p.precommanditem, b.bname, p.clarge, p.cmedium, p.csmall
FROM product p, brand b
WHERE p.clarge = #{cLarge}
AND p.cmedium = #{cMedium}
AND p.csmall = #{cSmall}
AND b.bno = p.bno

SELECT pcode, cproductcolor, cimageproduct1, cimageproduct2, cimageproduct3, ccolorchipimage
FROM color
WHERE pcode = #{pcode}
