mapper.update:
UPDATE shoppingbag
SET
sbproductcolor = #{sbproductcolor},
sbproductamount = #{sbproductamount},
sbproductsize = #{sbproductsize}
WHERE sbno = #{sbno}

mapper.select:
SELECT sbno
FROM shoppingbag
WHERE mno = #{mno}
AND pcode = #{pcode}
AND sbproductcolor = #{sbproductcolor}
AND sbproductsize = #{sbproductsize}
UNION ALL
SELECT -1 as sbno
FROM dual
WHERE NOT EXISTS( SELECT sbno
                  FROM shoppingbag
                  WHERE mno = #{mno}
                  AND pcode = #{pcode}
                  AND sbproductcolor = #{sbproductcolor}
                  AND sbproductsize = #{sbproductsize} )
