mapper.update:
UPDATE shoppingbag
SET
sbproductcolor = #{sbproductcolor},
sbproductamount = #{sbproductamount},
sbproductsize = #{sbproductsize}
WHERE sbno = #{sbno}

mapper.select:
select sbno
from shoppingbag
WHERE mno = #{mno}
AND pcode =
#{pcode}
AND sbproductcolor =
#{sbproductcolor}
AND sbproductsize =
#{sbproductsize}
union all
select -1 as sbno
from dual
where not
exists(select sbno
from shoppingbag
WHERE mno = #{mno}
AND pcode =
#{pcode}
AND sbproductcolor = #{sbproductcolor}
AND
sbproductsize =
#{sbproductsize})
